package com;

public class Ciclos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=0;
		
				while(x<5)
				{
					System.out.println("HELOO");
					x += 1;
				}
				x=0;
				
				do
				{
					System.out.println("HELOO1");
					x++;
				}while(x<5);
				
				for(int i =0; i<5 ;i++)
				{
					System.out.println(i + "VALOR");
				}

	}

}
